css:
body {
            font-family: Arial, sans-serif;
            line-height: 1.6;
            background-color: #f8f8f8; /* Light background for better contrast */
            color: #333; /* Darker text for better readability */
            margin: 0;
            padding: 0;
        }

        /* Container styling */
        .container {
            max-width: 800px;
            margin: 40px auto; /* Add some top and bottom margin */
            padding: 20px;
            background-color: #fff; /* White background for the container */
            border: 1px solid #ccc;
            border-radius: 8px; /* Slightly rounded corners */
            box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1); /* Subtle shadow for depth */
        }

        /* Header styling */
        .header {
            background-color: red; /* Green background */
            color: white; /* White text */
            padding: 15px;
            text-align: center;
            margin-bottom: 20px;
            border-bottom: 1px solid #ddd; /* Border for separation */
            border-radius: 8px 8px 0 0; /* Rounded top corners */
        }

        .header h1 {
            margin: 0;
            font-size: 24px; /* Larger font size for the header */
        }

        /* Content styling */
        .content {
            margin-bottom: 20px;
            padding: 20px;
            background-color: white;
            border-radius: 8px; /* Rounded corners */
            box-shadow: 0 2px 8px rgba(0, 0, 0, 0.05); /* Subtle shadow for depth */
        }

        /* Paragraph styling */
        .content p {
            margin: 10px 0;
            padding: 0;
        }

        .content p strong {
            color: #555; /* Slightly darker text color for labels */
        }
        
        .content img {
            max-width: 100%; /* Ensure the image does not exceed the width of the container */
            height: auto; /* Maintain aspect ratio */
            max-height: 300px; /* Limit the maximum height of the image */
            display: block; /* Center the image */
            margin: 10px auto; /* Add margin to the image */
        }

        /* Footer styling */
        .footer {
            text-align: center;
            font-style: italic;
            color: #666;
            margin-top: 20px;
            padding: 10px;
            border-top: 1px solid #ddd; /* Border for separation */
            border-radius: 0 0 8px 8px; /* Rounded bottom corners */
            background-color: #f0f0f0; /* Light gray background */
        }
