h3 {
  font-size: 18px;
  /*border-bottom: 1px solid #ddd;*/
  padding-bottom: 5px;
}

/* Transaction Information */
.transaction-info {
  margin-bottom: 20px;
}

.transaction-info p {
  margin: 5px 0;
  padding-bottom: 5px;
  font-weight: bold; /* Bold for labels */
}

table.child-table {
  width: 100%;
  border-collapse: collapse;
  margin-bottom: 20px;
  text-align: center;
}

table.child-table thead th {
  padding: 10px;
  text-align: center;
  background-color: #eee;
  border: 1px solid #ddd;
}

table.child-table tbody td {
  padding: 10px;
  border: 1px solid #ddd;
  text-align: center;
}
